import React, { Component } from "react";

const AddItem = ({ products, onSubmit }) => {
  const options = products.map(product => {
    return (
      <option key={product.id} value={product}>
        {product.name}
      </option>
    );
  });
  return (
    <form>
      <div classNam="form-group">
        >
        <label for="quantity">
          <h3>Quantity</h3>
        </label>
        <input type="text" name="quantity"></input>
      </div>
      <label for="products">
        <h3>Products</h3>
      </label>
      <select id="cars" name="cars">
        {options}
      </select>

      <input onClick={onSubmit} type="submit"></input>
    </form>
  );
};

export default AddItem;
