import React from "react";
import CartHeader from "./components/CartHeader";
import CartFooter from "./components/CartFooter";
import CartItems from "./components/CartItems";
import AddItem from "./components/AddItem";

import "./App.css";

import addableItems from "./data/addableItems";
import cartList from "./data/cartList";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: addableItems, //DONT NEEDS TO BE IN STATE
      cartItemsList: cartList
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(item) {
    //TODO: Check if item is already in cartList add them - if not increase quantity
    // const filteredItemsList = this.state.cartItemsList.filter(element => element.product.name === item.product.name);
    const index = this.state.cartItemsList.indexOf(item);
    console.log(index);

    console.log(filteredItemsList);

    let itemToAdd;
    if (filteredItemsList.length != 0) {
      itemToAdd = filteredItemsList[0];
      itemToAdd.quantity += item.quantity;
      this.setState(prevState => {

        return {
          cartItemsList: {...prevState.cartItemsList, itemToAdd}
        }
      })

    } else { 
        itemToAdd = {...item, id: this.state.cartItemsList.length + 1};
        this.setState(prevState => {
          return {
            cartItemsList: [...prevState.cartItemsList, itemToAdd]
          }
        })
      }
  }

  render() {
    return (
      <div className="App">
        <CartHeader />
        <CartItems cartData={this.state.cartItemsList} />
        <AddItem products={this.state.products} onSubmit={this.handleSubmit} />
        <CartFooter copyright="2020" />
      </div>
    );
  }
}

export default App;
