import React, { Component } from "react";

class AddItem extends Component {
  constructor({ products, onSubmit }) {
    super(products, onSubmit);

    this.state = {
      quantity: "",
      product: ""
    };
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    console.log(e);
  }

  render() {
    const options = this.products.map(product => {
      return (
        <option key={product.id} value={product}>
          {product.name}
        </option>
      );
    });

    return (
      <form>
        <div className="form-group">
          <label for="quantity">
            <h3>Quantity</h3>
          </label>
          <input
            onChange={this.handleChange}
            className="form-control"
            type="text"
            name="quantity"
            value={this.state.quantity}
          ></input>
        </div>
        <div className="form-group">
          <label for="product">
            <h3>Product</h3>
          </label>
          <select
            onChange={this.handleChange}
            className="form-control"
            name="product"
          >
            {options}
          </select>
        </div>

        <button
          className="btn btn-primary"
          onClick={handleSubmit}
          type="submit"
        >
          Submit
        </button>
      </form>
    );
  }
}

export default AddItem;
