import React, { Component } from "react";

const AddItem = ({ products, onSubmit }) => {
  const options = products.map(product => {
    return (
      <option key={product.id} value={product}>
        {product.name}
      </option>
    );
  });

  const handleSubmit = e => {
    e.preventDefault();
    console.log(e);
  };

  return (
    <form>
      <div className="form-group">
        <label for="quantity">
          <h3>Quantity</h3>
        </label>
        <input className="form-control" type="text" name="quantity"></input>
      </div>
      <div className="form-group">
        <label for="products">
          <h3>Products</h3>
        </label>
        <select className="form-control" name="products">
          {options}
        </select>
      </div>

      <button className="btn btn-primary" onClick={handleSubmit} type="submit">
        Submit
      </button>
    </form>
  );
};

export default AddItem;
